mapper.select:
select


        ${fields}





from user_royal_flow_pro




        and create_time >= #{map.startTime}


        and create_time <= #{map.endTime}


        and status != 0



    order by ${param.orderBy}

SELECT * from (
(SELECT f.id, f.audit_id as order_id, f.amount AS sec_amount, f.push_type, f.create_time, f.order_amount, u.nick, u.avatar, f.source_type as query_type, CASE f.audit_id WHEN f.order_id THEN 1 ELSE 2 END as type, null as city_name, null as name, f.source_id FROM user_royal_flow AS f LEFT JOIN user_info AS u ON u.id = f.user_id where !ISNULL(f.push_type) and f.user_id = #{map.map.userId} )
UNION ALL
(SELECT a.id, null as order_id, a.money AS sec_amount, CASE a.push_type WHEN 2 THEN (CASE a.us_id WHEN #{map.map.userId} THEN 1 ELSE 2 END) ELSE 1 END as push_type, a.create_time, a.money AS order_amount, u.nick,u.avatar, CASE a.type WHEN 1 THEN 2 ELSE 1 END as query_type, a.status as type, a.city_name, a.name, null as source_id FROM user_promoter_audit AS a LEFT JOIN user_info AS u ON u.id = a.us_id where a.status != 1 and a.open_type = 2 and (a.agent_id = #{map.map.userId} or a.us_id = #{map.map.userId}) )
UNION ALL (SELECT f.id, f.order_id, f.sec_amount, f.type as push_type, f.create_time, f.order_amount, u.nick, u.avatar, 4 as query_type, CASE f.status WHEN 1 THEN 2 ELSE 1 END type, null as city_name, null as name, null as source_id FROM user_royal_flow_pro AS f LEFT JOIN user_info AS u ON u.id = f.user_id WHERE f.agent_id = #{map.map.userId} and (f.status = 1 or f.status = 2) and ISNULL(f.order_id) )
UNION ALL
(SELECT f.id, f.order_id, f.sec_amount, f.type as push_type, f.create_time, f.order_amount, u.nick, u.avatar, 4 as query_type, CASE f.status WHEN 1 THEN 2 ELSE 1 END type, null as city_name, null as name, null as source_id FROM user_royal_flow_pro AS f LEFT JOIN user_info AS u ON u.id = f.user_id WHERE f.agent_id = #{map.map.userId} and
!ISNULL(f.order_id) and
(f.status = 1 or f.status = 2) )
) a
and type = #{map.map.type}
and push_type = #{map.map.pushType}
order by ${map.orderBy}

(
SELECT
f.id,f.order_id,f.sec_amount,f.type,f.create_time,f.order_amount,f.status,
u.nick,u.avatar,m.grade,1 as query_type,0 as source_type,
(CASE m.grade WHEN 0 THEN mc.user_direct WHEN 1 THEN mc.gold_direct WHEN 2 THEN mc.partner_direct WHEN 3 THEN mc.super_partner_direct END) power,
(CASE m.grade WHEN 0 THEN mc.user_indirect WHEN 1 THEN mc.gold_indirect WHEN 2 THEN mc.partner_indirect WHEN 3 THEN mc.super_partner_indirect END) in_power,
(CASE m.grade WHEN 0 THEN mc.gold_team WHEN 1 THEN mc.partner_team WHEN 2 THEN mc.super_partner_team WHEN 3 THEN NULL END) up_grade_team,
(CASE m.grade WHEN 0 THEN mc.gold_person WHEN 1 THEN mc.partner_person WHEN 2 THEN mc.super_partner_person WHEN 3 THEN NULL END) up_grade_person
FROM
user_royal_flow_pro AS f
LEFT JOIN user_info AS u ON u.id = f.agent_id
inner JOIN tenant_invite AS m ON m.user_id = f.agent_id and m.tenant_id = f.tenant_id
LEFT JOIN tenant_invite_config mc ON mc.tenant_id = f.tenant_id
WHERE f.status != 0

    and f.type = #{map.type}


    and f.order_id = #{map.orderId}


    and f.create_time >= #{map.startTime}


    and f.create_time <= #{map.endTime}


    and f.status = #{map.status}


    and f.city_code = #{map.cityCode}

)
union all
(
SELECT
f.id,f.order_id,f.sec_amount AS sec_amount,f.type as type,f.create_time,f.order_amount,
2 as status,
u.nick,u.avatar,u.level as grade,2 as query_type,14 as source_type,
0 as power,0 as in_power,0 as up_grade_team,0 as up_grade_person
FROM
user_royal_flow_pro AS f
LEFT JOIN user_info AS u ON u.id = f.agent_id
where

ISNULL( f.order_id ) and

!ISNULL(f.type)

    and f.type = #{map.type}


    and f.create_time >= #{map.startTime}


    and f.order_id = #{map.orderId}


    and f.create_time <= #{map.endTime}


    and f.city_code = #{map.cityCode}

)

    union all
    (
    SELECT
    f.id,f.order_id,f.amount AS sec_amount,f.push_type as type,f.create_time,f.order_amount,
    2 as status,
    u.nick,u.avatar,u.level as grade,2 as query_type,f.source_type,
    0 as power,0 as in_power,0 as up_grade_team,0 as up_grade_person
    FROM
    user_royal_flow AS f
    LEFT JOIN user_info AS u ON u.id = f.user_id
    where !ISNULL(f.push_type)

        and f.push_type = #{map.type}


        and f.create_time >= #{map.startTime}


        and f.order_id = #{map.orderId}


        and f.create_time <= #{map.endTime}


        and f.city_code = #{map.cityCode}

    )

order by create_time desc
